using ll = long long;
typedef pair<ll,ll>PII;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<PII>sum;
        for(auto tt : verticalCut){
            sum.push_back({tt, 0});
        }

        for(auto tt : horizontalCut){
            sum.push_back({tt, 1});
        }
        sort(sum.begin(),sum.end(), greater<PII>());
        ll ans = 0, t1 = 1, t0 = 1;
        for(auto[x, y] : sum){
            if(y)ans += x * t0, t1 ++;
            else ans += x * t1, t0 ++;
            // printf("ans = %d\n", ans);
        }
        return ans;
    }
};
