public class Solution {
    public long MinimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        var list = new List<int[]>();
        for(int i = 0; i < m-1;i++)
            list.Add(new int[]{horizontalCut[i],0});
        for(int i = 0; i < n-1;i++)
            list.Add(new int[]{verticalCut[i],1});
        list.Sort((a,b)=>b[0]-a[0]);
        int hCutTime = 1,vCutTime=1;
        long sum = 0;
        foreach(var el in list)
        {
            int val = el[0];
            int t = el[1];
            if(t == 0)
            {
                sum += (long)val*hCutTime;
                vCutTime++;
            }
            else
            {
                sum += (long)val*vCutTime;
                hCutTime++;
            }
        }
        return sum;
    }
}
