class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(v.rbegin(),v.rend());
        sort(h.rbegin(),h.rend());
        long long hc=1,vc=1,c=0,i=0,j=0;
        while(i<h.size()&&j<v.size()){
            if(h[i]>=v[j]){
                c+=h[i]*vc;
                hc++;i++;
            }
            else{
                c+=v[j]*hc;
                vc++;j++;
            }
        }
        while(i<h.size()){
            c+=h[i]*vc;
            hc++;i++;
        }
        while(j<v.size()){
            c+=v[j]*hc;
            vc++;j++;
        }
        return c;
    }
};
