class Solution {
public:
    #define ll long long
    #define pii pair<int,int>
#define all(arr) arr.begin(),arr.end()
#define fr first
#define sc second
#define pb push_back
    long long minimumCost(int n, int m, vector<int>& hCut, vector<int>& vCut) {
        priority_queue<pair<ll,ll>>pq;
        for(int i=0; i<n-1; i++){
            pq.push({hCut[i], 0ll}); //cost, typ
        }
        for(int i=0; i<m-1; i++){
            pq.push({vCut[i], 1ll}); //cost,  typ 1= vCut
        }
        ll ans=0ll;
        ll h=1, v=1;
        while(!pq.empty()){
            auto p= pq.top();
            pq.pop();
            ll typ= p.sc;
            ll cost= p.fr;
            ans+= (typ?(h*1ll*cost):(v*1ll*cost));
            if(typ)v++;
            else h++;
        }
        return ans;
    }
};
