#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        ll i=0,j=0,h1=1,v1=1,ans=0;
        while(i<m-1 && j<n-1)
        {
            if(h[i]<v[j])
            {
                ll calci=v[j]*h1;
                ans+=calci;
                v1++;
                j++;
            }
            else 
            {
                ll calci=h[i]*v1;
                ans+=calci;
                i++;
                h1++;
            }
        }
        while(j<n-1)
        {

            ll calci=v[j]*h1;
            ans+=calci;
            j++;
        }
        while(i<m-1)
        {
            ll calci=h[i]*v1;
            ans+=calci;
            i++;
        }
        return ans;

    }
};
