class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        h = v = 1
        res = 0
        while h < m and v < n:
            if horizontalCut[m-1-h] >= verticalCut[n-1-v]:
                res += horizontalCut[m-1-h] * v
                h += 1
            else:
                res += verticalCut[n-1-v] * h
                v += 1
        else:
            while h < m:
                res += horizontalCut[m-1-h] * v
                h += 1
            while v < n:
                res += verticalCut[n-1-v] * h
                v += 1
        return res
