typedef long long ll;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(),hc.end());
        sort(vc.begin(),vc.end());

        long long i=m-2,j=n-2;
        long long vcnt=1,hcnt=1;
        long long ans=0;

        while(i>=0 and j>=0){
            if(hc[i]>vc[j]){
                ans+=(hc[i]*vcnt);
                hcnt++;
                i--;
            }
            else{
                ans+=(vc[j]*hcnt);
                vcnt++;
                j--;
            }
        }
        while(i>=0){
                ans+=(hc[i]*vcnt);
                i--;
        }
        while(j>=0){
                ans+=(vc[j]*hcnt);
                j--;
        }


        return ans;
    }
};
