class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> que;
        long long ans=0;
        for(int i=0;i<m-1;i++)
        {
            auto i1=make_pair(1,horizontalCut[i]);
            que.push_back(i1);
        }
        for(int i=0;i<n-1;i++)
        {
            auto i1=make_pair(2,verticalCut[i]);
            que.push_back(i1);
        }
        sort(que.begin(),que.end(),[&](pair<int,int> a,pair<int,int> b){
            return a.second>b.second;
        });
        int heng=0,shu=0;
        for(int i=0;i<que.size();i++)
        {
            if(que[i].first==1)
            {
                ans=ans+(que[i].second*(shu+1));
                heng++;
            }
            else if(que[i].first==2)
            {
                ans=ans+(que[i].second*(heng+1));
                shu++;
            }

        }
        return ans;

    }
};
