class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int, char>>v;
        for(auto i : horizontalCut){
            v.push_back({i, 'H'});
        }
        for(auto i : verticalCut){
            v.push_back({i, 'V'});
        }
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        long long int ans = 0;
        int vertical = 1;
        int horzntl = 1;
        for(auto i : v){
            char c = i.second;
            int cost = i.first;
            if(c == 'H'){
                ans += cost * vertical;
                horzntl++;
            }
            else{
                ans += cost * horzntl;
                vertical++;
            }
        }

        return ans;
    }
};
