class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,char>>p;
     int l=verticalCut.size();
      for(int i=0;i<l;i++)
      {
        p.push_back({verticalCut[i],'v'});
      }
      int l1=horizontalCut.size();
      for(int i=0;i<l1;i++)
      {
        p.push_back({horizontalCut[i],'h'});
      }  
      long long total=0;
      sort(p.rbegin(),p.rend());
      long long x1=1,x3=1;
      for(auto it:p)
      {
        if(it.second=='h')
        {
            total+=(static_cast<long long>(it.first)*x3);
            x1++;
        }
        else
        {
            total+=(static_cast<long long>(it.first)*x1);
            x3++;
        }
      }
    return total; 
    }
};
