class Solution {
public:
    long long minimumCostOfCutting(vector<int>x, vector<int>y, int m, int n)
{
    long long res = 0;
    sort(x.begin(),x.end(), greater<int>());
    sort(y.begin(), y.end(), greater<int>());
    int hz = 1, vert = 1,i = 0, j = 0;
    while (i < m && j < n)
    {
        if (x[i] > y[j])
        {
            res += x[i] * vert;
            hz++;
            i++;
        }
        else
        {
            res += y[j] * hz;
            vert++;
            j++;
        }
    }
    long long total = 0;
    while (i < m) total += x[i++];
    res += total * vert;

    total = 0;
    while (j < n) total += y[j++];
    res += total * hz;

    return res;
}
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        return minimumCostOfCutting(horizontalCut,verticalCut,m-1,n-1);
    }
};
