class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hori, vector<int>& ver) {
        sort(hori.rbegin(), hori.rend());
        sort(ver.rbegin(), ver.rend());
        long long h = 1;long long v = 1; 
        int i = 0, j = 0; 
        long long cost = 0;

        while (i < hori.size() && j < ver.size()) {
            if (hori[i] > ver[j]) {
                cost +=(long long)( hori[i] * v);
                h++;
                i++;
            } else {
                cost += (long long )(ver[j] * h);
                v++;
                j++;
            }
        }

        while (i < hori.size()) {
            cost += (1LL)*(hori[i] * v);
            h++;
            i++;
        }

        while (j < ver.size()) {
            cost += (1LL)*(ver[j] * h);
            v++;
            j++;
        }

        return cost;

    }
};
