class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        res = 0
        finalSorted = [[c, 'h'] for c in horizontalCut]
        finalSorted.extend([[c, 'v'] for c in verticalCut])
        finalSorted.sort(reverse=True)
        hor, ver = 1, 1
        for i in range(len(finalSorted)):
            arr = finalSorted[i][1]
            if arr == 'h':
                res += finalSorted[i][0] * hor
                ver += 1
            else:
                res += finalSorted[i][0] * ver
                hor += 1
        return res
