impl Solution {
    pub fn minimum_cost(m: i32, n: i32, mut TLE: Vec<i32>, mut PR: Vec<i32>) -> i64 {
        TLE.sort_by(|a, b| b.cmp(a));
        PR.sort_by(|a, b| b.cmp(a));

        let mut RE = 0_i64;
        let mut AE = 0;
        let mut PR_index = 0;
        let mut AE_pieces = 1;
        let mut PR_pieces = 1;

        while AE < TLE.len() && PR_index < PR.len() {
            if TLE[AE] >= PR[PR_index] {
                RE += TLE[AE] as i64 * PR_pieces;
                AE_pieces += 1;
                AE += 1;
            } else {
                RE += PR[PR_index] as i64 * AE_pieces;
                PR_pieces += 1;
                PR_index += 1;
            }
        }


        while AE < TLE.len() {
            RE += TLE[AE] as i64 * PR_pieces;
            AE += 1;
        }

        while PR_index < PR.len() {
            RE += PR[PR_index] as i64 * AE_pieces;
            PR_index += 1;
        }

        RE
    }
}
