class Solution:
    def minimumCost(self, m: int, n: int, ho: List[int], ve: List[int]) -> int:
        ho.sort()
        ve.sort()
        a = b = 1
        m -= 2
        n -= 2
        ans = 0
        while m >= 0 and n >= 0:
            if ho[m] > ve[n]:
                ans += ho[m] * b
                a += 1
                m -= 1
            else:
                ans += ve[n] * a
                b += 1
                n -= 1
        return ans + sum(ho[i] * b for i in range(m + 1)) + sum(ve[i] * a for i in range(n + 1))
