class Solution {
    public long minimumCost(int m, int n, int[] hr, int[] vc) {
        // Sort h and v in descending order
        long h[] = new long[m-1];
        for(int i=0;i<m-1;i++){
            h[i]=hr[i];
        }
        long v[] = new long[n-1];
        for(int i=0;i<n-1;i++){
            v[i]=vc[i];
        }
        Arrays.sort(h);
        Arrays.sort(v);

        // Reverse the sorted arrays to get descending order
        reverseArray(h);
        reverseArray(v);

        long totalCost = 0;
         int hi = 0;
        int vi = 0;
        long hp = 1;
        long vp = 1;

        while (hi < h.length && vi < v.length) {
            if (h[hi] >= v[vi]) {
                totalCost += h[hi] * vp;
                hi++;
                hp++;
            } else {
                totalCost += v[vi] * hp;
                vi++;
                vp++;
            }
        }

        while (hi < h.length) {
            totalCost += h[hi] * vp;
            hi++;
        }

        while (vi < v.length) {
            totalCost += v[vi] * hp;
            vi++;
        }

        return totalCost;
    }

    private void reverseArray(long[] array) {
        int left = 0;
        int right = array.length - 1;
        while (left < right) {
            long temp = array[left];
            array[left] = array[right];
            array[right] = temp;
            left++;
            right--;
        }
    }
    }
