class Solution {
public:
    #define ll long long
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        ll ans=0;
        priority_queue<pair<int,int>> pq;
        ll x=1,y=1;
        for(auto it:h){
            pq.push({it,0});
        }
        for(auto it:v){
            pq.push({it,1});
        }

        while(!pq.empty()){
            auto tp=pq.top();
            pq.pop();

            int cur=tp.first;
            int type=tp.second;

            if(type==0){
                ans+=cur*y;
                x++;
            }
            else{
                ans+=cur*x;
                y++;
            }
        }

        return ans;

    }
};
