class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
    sort(hc.begin(), hc.end());
    reverse(hc.begin(), hc.end());
    sort(vc.begin(), vc.end());
    reverse(vc.begin(), vc.end());
    long long i = 0;
    long long j = 0;
    long long h = 0;
    long long v = 0;
    long long ans = 0;
    long long n1 = hc.size();
    long long n2 = vc.size();
    while (i < n1 || j < n2) {
        if (i < n1 && j<n2 && vc[j]>hc[i]) {
            ans = ans + vc[j] * (h + 1);
            v++;
            j++;
        }
        else if (i < n1 && j < n2 && vc[j] <= hc[i]) {
            ans = ans + hc[i] * (v + 1);
            h++;
            i++;
        }
        else {
            if (i < n1) {
                ans = ans + hc[i] * (v + 1);
                h++;
                i++;
            }
            if (j < n2) {
                ans = ans + vc[j] * (h + 1);
                v++;
                j++;
            }
        }
    }
    return ans;
}
};
