class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         std::sort(horizontalCut.begin(), horizontalCut.end(), std::greater<int>());
        std::sort(verticalCut.begin(), verticalCut.end(), std::greater<int>());

        long long totalCost = 0;
        int hIndex = 0, vIndex = 0;
        int horizontalPieces = 1, verticalPieces = 1;


        while (hIndex < horizontalCut.size() && vIndex < verticalCut.size()) {
            if (horizontalCut[hIndex] > verticalCut[vIndex]) {
                totalCost += static_cast<long long>(horizontalCut[hIndex]) * verticalPieces;
                horizontalPieces++;
                hIndex++;
            } else {
                totalCost += static_cast<long long>(verticalCut[vIndex]) * horizontalPieces;
                verticalPieces++;
                vIndex++;
            }
        }


        while (hIndex < horizontalCut.size()) {
            totalCost += static_cast<long long>(horizontalCut[hIndex]) * verticalPieces;
            hIndex++;
        }


        while (vIndex < verticalCut.size()) {
            totalCost += static_cast<long long>(verticalCut[vIndex]) * horizontalPieces;
            vIndex++;
        }

        return totalCost;
    }
};
