class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        long long int ans = 0;
        sort(a.begin(),a.end(),greater<int>());
        sort(b.begin(),b.end(),greater<int>());
        int j=0;
        for(int i=0;i<m-1;i++)
        {
            while(j<n-1 && a[i]<b[j])
            {
                ans += (i+1)*1ll*b[j];
                j++;
            }
            ans +=(j+1)*1ll*a[i];
        }
        while(j<n-1)
        {
            ans += m*1ll*b[j];
            j++;
        }
        return ans;
    }
};
