class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end(), greater<int>());
        sort(vc.begin(), vc.end(), greater<int>());
        int hi = 0, vi = 0;
        int hSegments = 1, vSegments = 1;
        long long int ans = 0;
        while (hi < hc.size() && vi < vc.size()) {
            if (hc[hi] >= vc[vi]) {
                ans += hc[hi] * vSegments;
                hSegments++;
                hi++;
            } 
            else {
                ans += vc[vi] * hSegments;
                vSegments++;
                vi++;
            }
        }

        while (hi < hc.size()) {
            ans += hc[hi] * vSegments;
            hSegments++;
            hi++;
        }

        while (vi < vc.size()) {
            ans += vc[vi] * hSegments;
            vSegments++;
            vi++;
        }
        return ans;
    }
};
