class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans = 0, h = 1, v = 1;
        vector<pair<int,int>> cuts;

        for(auto &v : horizontalCut)
            cuts.push_back({v, 0});
        for(auto &v : verticalCut)
            cuts.push_back({v, 1});

        sort(cuts.begin(), cuts.end(), greater<pair<int, int>>());

        for(auto &val : cuts){
            //cout << val.first << " " << val.second << "\n";
            if(val.second){
                ans += h*val.first;
                ++v;
            }else{
                ans += v*val.first;
                ++h;
            }
        }

        return ans;
    }
};
