class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());

        int hi=0;
        int vi=0;
        int hp=1;
        int vp=1;

        long long ans=0;

        int n1=h.size();
        int n2=v.size();

        while(hi<n1 && vi<n2){
            if(h[hi]>v[vi]){
                ans+=(h[hi] * vp);
                hp++;
                hi++;
            }
            else{
                ans+=(v[vi] * hp);
                vp++;
                vi++;
            }
        }

        while(hi<n1){
            ans+=(h[hi]*vp);
            hi++;
        }
        while(vi<n2){
            ans+=(v[vi]*hp);
            vi++;
        }

        return ans;
    }
};
