class Solution {
public:
   long long solve(int m, int n, vector<int>& h, vector<int>& v){
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int i = h.size()-1;
        int j = v.size()-1;
        set<int>s;
        set<int>s1;
        long long ans = 0;
        while(i>=0&&j>=0){
            if(v[j]>h[i]){
                ans+=v[j]*(s.size()+1);
                s1.insert(j);
                j--;
            }
            else{
                ans+=h[i]*(s1.size()+1);
                s.insert(i);
                i--;
            }
        }
        while(i>=0){
            ans+=h[i]*(n);
                i--;
        }
        while(j>=0){
            ans+=(v[j]*m);
            j--;
        }
        return ans;
   }
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        return solve(m,n,h,v);
    }
};
