class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        long long c1=0;
        long long c2=0;
        long long cost=0;
        priority_queue<long long>h;
        priority_queue<long long>v;
        for(int i=0;i<hor.size();i++){
            h.push(hor[i]);
        }
        for(int i=0;i<ver.size();i++){
            v.push(ver[i]);
        }
        while(!h.empty() && !v.empty()){
            if(h.top()>v.top()){
                cost+=h.top()*(c2+1);
                c1++;
                h.pop();
            }else{
                cost+=v.top()*(c1+1);
                v.pop();
                c2++;
            }
        }
        while(!h.empty()){
            cost+=h.top()*(c2+1);
            c1++;
            h.pop();
        }
        while(!v.empty()){
            cost+=v.top()*(c1+1);
            c2++;
            v.pop();            
        }
        return cost;   
    }
};
