bool Compare1(pair<long long int,int>&p1,pair<long long int,int>&p2){
    if(p1.first!=p2.first) return p1.first>p2.first;
    return p1.second<p2.second;
}


bool Compare2(pair<long long int,int>&p1,pair<long long int,int>&p2){
    if(p1.first!=p2.first) return p1.first>p2.first;
    return p1.second>p2.second;
}

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long int,int>>vp;
        for(int i=0;i<m-1;i++) vp.push_back({horizontalCut[i],0});
        for(int i=0;i<n-1;i++) vp.push_back({verticalCut[i],1});
        int currX = 1;
        int currY = 1;
        sort(vp.begin(),vp.end(),Compare1);
        long long cost = 0;
        for(auto it:vp){
            int val = it.first;
            int cutType = it.second;
            if(cutType==0){
                currX++;
                cost+=(val*currY);
            }else{
                currY++;
                cost+=(val*currX);
            }
        }
        sort(vp.begin(),vp.end(),Compare2);
        long long cost1 = 0;
        currX = 1;
        currY = 1;
        for(auto it:vp){
            long long val = it.first;
            int cutType = it.second;
            if(cutType==0){
                currX++;
                cost1+=(val*currY);
            }else{
                currY++;
                cost1+=(val*currX);
            }
        }
        return min(cost,cost1);
    }
};
