class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) 
    {
        long long res = 0;
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());

        int i = 0, j = 0;

        while(i<m-1 || j<n-1)
        {
            if(i<m-1 && j<n-1)
            {
                if(h[i]>v[j])
                {
                    res += (j+1)*h[i];
                    i++;
                }
                else
                {
                    res += (i+1)*v[j];
                    j++;
                }
            }
            else if(i<m-1)
            {
                res += (j+1)*h[i];
                i++;
            }
            else if(j<n-1)
            {
                res += (i+1)*v[j];
                j++;
            }
            else
            {
                break;
            }
        }

        return res;
    }
};
