#define ll long long 
class Solution {
public:
    ll minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pair<ll,ll>> vp; // 1 for h, 2 for vc
        for(int i=0; i<m-1; i++){
            vp.push_back({hc[i],1});
        }
        for(int i=0; i<n-1; i++){
            vp.push_back({vc[i],2});
        }

        sort(vp.begin(), vp.end(), greater<pair<ll,ll>>());

        ll h=1,v=1;
        ll cost=0;

        for(auto p : vp){
            if(p.second == 1){
                cost += p.first * v;
                h++;
            }
            else{
                cost += p.first * h;
                v++;
            }
        }

        return cost;
    }

};
