class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.begin(), hCut.end(), greater<int>());
        sort(vCut.begin(), vCut.end(), greater<int>());

        long long res = 0;
        int hi = 0, vi = 0;
        for (int i = 0; i < hCut.size() + vCut.size(); i++) {
            if (hi >= hCut.size()) {
                res += (hi + 1) * vCut[vi++];
            } else if (vi >= vCut.size()) {
                res += (vi + 1) * hCut[hi++];
            } else if (hCut[hi] >= vCut[vi]) {
                res += (vi + 1) * hCut[hi++];
            } else {
                res += (hi + 1) * vCut[vi++];
            }
        } 

        return res;
    }
};
