class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hz, vector<int>& vc) {
        long long ans = 0;
        sort(hz.begin(),hz.end(),greater<int>());
        sort(vc.begin(),vc.end(),greater<int>());
        int i = 0;
        int j= 0;
        int hh = 1;
        int vv = 1;

        if(m==1) {
            for(int j = 0;j<n-1;j++) ans+= vc[j];
            return ans;
        }

        if(n==1){
            for(int j =0 ;j<m-1;j++) ans+= hz[j];
            return ans;
        }
        while(i<m && j<n-1){
           if(hz[i]>vc[j]){
               ans+= hz[i]*vv;
               hh++;
               i++;
           }
            else if(hz[i]<=vc[j]){
               ans+= vc[j]*hh;
                vv++;
                j++;
            }

            if(i==m-1) {
                while(j<n-1) ans+= vc[j++]*hh;
                break;
            }

            if(j==n-1){
                while(i<m-1) ans+= hz[i++]*vv;
                break;
            }
        }

        return ans;
    }
};
