class Solution {
public:
   # define ll long long
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
                ll v=0,h=0;

        sort(hc.rbegin(),hc.rend());
        sort(vc.rbegin(),vc.rend());

        int i=0,j=0;
        ll ans=0;
        while(i<m-1 && j<n-1){
            if(hc[i]==vc[j]){
               ans+=(hc[i])*1LL*(v+1);
               h++;
               i++;
            }
            else if(hc[i]<vc[j]){
               ans+=(vc[j])*1LL*(h+1);
               v++;
               j++;
            }
            else{
                 ans+=(hc[i])*1LL*(v+1);
               h++;
               i++;
            }
        }

        while(i<m-1){
             ans+=(hc[i])*1LL*(v+1);
               h++;
               i++;
        }
        while(j<n-1){
            ans+=(vc[j])*1LL*(h+1);
            v++; j++;
        }

        return ans;
    }
};
