class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        PriorityQueue<Integer> hlist = new PriorityQueue<Integer>((a,b)->b-a);
        PriorityQueue<Integer> vlist = new PriorityQueue<Integer>((a,b)->b-a);

        int addh = 0;
        int addv = 0;
        int htotal = 0;
        int vtotal = 0;
        for(int i = 0; i < horizontalCut.length; i++) {
            hlist.add(horizontalCut[i]);
            htotal += horizontalCut[i];
        }

        for(int i = 0; i < verticalCut.length; i++) {
            vlist.add(verticalCut[i]);
            vtotal += verticalCut[i]; 
        }
        // System.out.println(vlist + " " + hlist);

        long result = 0L;
        while( !hlist.isEmpty() || !vlist.isEmpty() ) {
            int maxh = -1;
            if ( !hlist.isEmpty()) {
                maxh = hlist.peek();
            }

            int maxv = -1;
            if ( !vlist.isEmpty()) {
                maxv = vlist.peek();
            }

            if ( maxh > maxv) {
                long val = hlist.poll();
                result += val * ( 1 + addh);
                addv++;
                htotal -= val;
            } else if ( maxh < maxv) {
                long val = vlist.poll();
                result += val * ( 1 + addv);
                addh++;
                vtotal -= val;
            } else{
                if ( htotal > vtotal ) {
                    long val = hlist.poll();
                    result += val * ( 1 + addh);
                    addv++;
                    htotal -= val;
                } else  {
                    long val = vlist.poll();
                    result += val * ( 1 + addv);
                    addh++;
                    vtotal -= val;
                }
            }
        }

        // System.out.println(vlist + " " + hlist);
        return result;
    }
}
