using ll = long long;
using vi = vector<int>;
using vvi = vector<vi>;
using vl = vector<ll>;
using vvl = vector<vl>;

#define sz(x) (int)(x).size()
#define all(x) begin(x), end(x)
#define rall(x) rbegin(x), rend(x)
#define pb push_back
#define eb emplace_back
#define mp make_pair
#define fi first
#define se second
#define lb lower_bound
#define ub upper_bound
#define ins insert
#define pq priority_queue
#define endl '\n'
#define rep(i, a, b) for(int i = a; i < b; i++)
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(rall(hc));
        sort(rall(vc));

        ll i= 0, j =0;
        ll ans = 0;
        ll hcuts =1;
        ll vcuts = 1;
        while(i<hc.size() && j < vc.size()){
            if(vc[j] > hc[i]){
                vcuts++;
                ans += hcuts * vc[j++];
            }
            else{
                hcuts++;
                ans += vcuts * hc[i++];
            }
        }
        while(i < hc.size()){
            ans += vcuts* hc[i++];
        }
        while(j < vc.size()) ans += hcuts * vc[j++];
        return ans;
    }
};
