class Solution {
public:
    using lll = long long;

    lll minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        lll ans = 0;
        int x = 1, y = 1,r=0,c=0;
        while (r < h.size() && c < v.size()) {
            if (h[r] >= v[c]) {
                ans += (lll)h[r] * y;
                x++;
                r++;
            } else {
               ans += (lll)v[c] * x;
                y++;
                c++;
            }
        }
        while (r < h.size()) {
            ans += (lll)h[r] * y;
            x++;
            r++;
        }
        while (c < v.size()) {
            ans += (lll)v[c] * x;
            y++;
            c++;
        }
        return ans;
    }
};
