class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        vector<vector<long long>> cost;

        for(auto e : hCut)
            cost.push_back({e, 0});
        for(auto e : vCut)
            cost.push_back({e, 1});

        sort(cost.begin(), cost.end());

        long long ans = 0, h = 1, v = 1;
        reverse(cost.begin(), cost.end());
        for(auto e : cost) {
            if(e[1] == 0) { // hCut
                ans += e[0] * v;
                h++;
            } else {
                ans += e[0] * h;
                v++;
            }
        }

        return ans;
    }
};
