class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {
        int h_cut = 0, c_cut = 0;
        sort(hC.rbegin(), hC.rend());
        sort(vC.rbegin(), vC.rend());
        int i = 0, j = 0;
        long long cost = 0;
        while (i < m-1 && j < n-1) {
            if (hC[i] > vC[j]) {
                cost += (long long)hC[i] * (j+1);
                ++i;
            }
            else if (hC[i] < vC[j]) {
                cost += (long long)vC[j] * (i+1);
                ++j;
            }
            else {
                cost += (long long)hC[i] * (i+j+3);
                ++i;
                ++j;
            }
        }
        while (i < m-1)
            cost += (long long)hC[i++] * n;
        while (j < n-1)
            cost += (long long)vC[j++] * m;
        return cost;
    }
};
