class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
        long long i, pa = 0, pb = 0;
        long long res = 0;
        sort(a.begin(), a.end());
        reverse(a.begin(), a.end());
        sort(b.begin(), b.end());
        reverse(b.begin(), b.end());
        for (i = 0; i < m + n - 2; ++i)
            if (pa < a.size() && (pb >= b.size() || a[pa] > b[pb])) res += 1LL * a[pa++] * (pb + 1);
        else res += 1LL * b[pb++] * (pa + 1);
        return res;
    }
};
