/**
 * @param {number} m
 * @param {number} n
 * @param {number[]} horizontalCut
 * @param {number[]} verticalCut
 * @return {number}
 */
var minimumCost = function(m, n, horizontalCut, verticalCut) {
    let a=new Array(m+n-2);
    for(let i=0;i<m-1;i++)
        a[i]=[horizontalCut[i],0];
    for(let i=0;i<n-1;i++)
        a[i+m-1]=[verticalCut[i],1];
    a.sort((a,b)=>b[0]-a[0]);
    let h=0,v=0,sum=0;
    for(let u of a){
        if(u[1]==0){
            sum+=u[0]*(v+1);
            h++;
        }else{
            sum+=u[0]*(h+1);
            v++;
        }
    }
    return sum;
};
