class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
         sort(hor.rbegin(),hor.rend());        
        sort(ver.rbegin(),ver.rend());
        int l=0,r=0;
        long long ans=0;
        int hcuts=1, vcuts=1;
        while(l<hor.size() && r<ver.size())
        {
            if(hor[l]<=ver[r])
            {
                ans+=1LL*ver[r]*hcuts;
                ++vcuts;
                ++r;
            }
            else
            {
                ans+=1LL*hor[l]*vcuts;
                ++hcuts;
                ++l;
            }
        }
        while(l<hor.size())
        {
            ans+=1LL*hor[l]*vcuts;
            ++l;
        }
        while(r<ver.size())
        {
            ans+=1LL*ver[r]*hcuts;
            ++r;
        }
        return ans;
    }
};
