class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        sort(hcut.rbegin(),hcut.rend());
        sort(vcut.rbegin(),vcut.rend());
        long long hc=1,vc=1,res=0;
        int i,j;
        for(i=0,j=0;i<m-1 && j<n-1;){
            if(hcut[i]>vcut[j]){
                res+=(long long)hcut[i]*vc;
                hc++,i++;
            }
            else{
                res+=(long long)vcut[j]*hc;
                vc++,j++;
            } 
        }
        if(i<m-1){
            long long rem=0;
            for(;i<m-1;++i) rem+=hcut[i];
            res+=rem*vc;
        }
        else{
            long long rem=0;
            for(;j<n-1;++j) rem+=vcut[j];
            res+=rem*hc;
        }
        return res;
    }
};
