class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans = 0;
        priority_queue<pair<long long,char>> pq;
        for(long long i:h) pq.push({i,'h'});
        for(long long j:v) pq.push({j,'v'});
        long long a = 1; long long b = 1;
        while(!pq.empty()){
            auto p = pq.top();
            pq.pop();
            if(p.second == 'h'){
                ans += p.first * b;
                a++;
            }else{
                ans += p.first * a;
                b++;
            }
        }
        return ans;
    }
};
