class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        res = 0
        horizontalCut.sort()
        verticalCut.sort()

        h_cnt = 1
        v_cnt = 1

        while horizontalCut or verticalCut:
            if (horizontalCut and verticalCut and horizontalCut[-1] > verticalCut[-1]) or not verticalCut:
                res += horizontalCut.pop() * v_cnt
                h_cnt += 1
            else:
                res += verticalCut.pop() * h_cnt
                v_cnt += 1

        return res
