class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        sort(hcut.begin(), hcut.end(),greater<int>());
        sort(vcut.begin(), vcut.end(),greater<int>());
        int i = 0, j = 0; 
        long long hCount = 1, vCount = 1;
        long long ans = 0;
        while (i < hcut.size() && j < vcut.size()) {
            if (hcut[i] > vcut[j]) {
                ans += 1LL*hcut[i] * vCount;
                i++;
                hCount++;
            } else {
                ans += 1LL*vcut[j] * hCount;
                j++;
                vCount++;
            }
        }
        while (i < hcut.size()) {
            ans += 1LL*hcut[i] * vCount;
            i++;
            hCount++;
        }
        while (j < vcut.size()) {
            ans += 1LL*vcut[j] * hCount;
            j++;
            vCount++;
        }
        return ans;
    }
};
