class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontal, vector<int>& vertical) {
        priority_queue<long long>ver,hor;
        long long verticals=1;
        long long horizontals=1;
        long long ans=0;
        for (auto it:vertical){ ver.push(it);}
        for (auto it:horizontal) {hor.push(it);}
        while(!ver.empty() && !hor.empty())
        {
        if(hor.top()>ver.top())
        {
          ans=ans+1LL*(hor.top()*verticals);
          hor.pop();
          horizontals++;
        }
        else{
          ans=ans+(1LL*ver.top()*horizontals);
          ver.pop();
          verticals++;
        }
        }
        while(!hor.empty())
        {
            ans=ans+1LL*(hor.top()*verticals);
            hor.pop();
        }
        while(!ver.empty())
        {
            ans=ans+(1LL*ver.top()*horizontals);
            ver.pop();
        }
        return ans;
    }
};
