class Solution {
public:
    typedef long long ll;
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        ll h=0,v=0;

        priority_queue<pair<ll,char>> q;

        for(auto x : horizontalCut){
            q.push({x,'h'});
        }

        for(auto x : verticalCut){
            q.push({x,'v'});
        }

        ll ans = 0;

        while(!q.empty()){
            auto it = q.top();
            q.pop();

            ll cost = it.first;
            char c = it.second;
            ll cuts;

            if(c=='h'){
                cuts = v+1;
                ans += cuts*cost;
                h++;
            }else{
                cuts = h+1;
                ans += cuts*cost; 
                v++;
            }
        }

        return ans;
    }
};
