class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {

        long long h1=1,v1=1;
        long long res=0;

        priority_queue<int> h(horizontalCut.begin(),horizontalCut.end());
        priority_queue<int> v(verticalCut.begin(),verticalCut.end());

        while(!h.empty() || !v.empty()){
            if(v.empty() || (!h.empty() && h.top() > v.top()) ){
                res+=(h.top() * v1);
                h1++;
                h.pop();
            }
            else{
                res+=(v.top() * h1);
                v1++;
                v.pop();
            }
        }
        return res;
    }
};
