class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        h_cnt = v_cnt = 0
        h_sum, v_sum = sum(horizontalCut), sum(verticalCut)
        all_cost = 0
        while horizontalCut and verticalCut:
            if horizontalCut[-1] > verticalCut[-1]:
                all_cost += horizontalCut.pop() * (v_cnt + 1)
                h_cnt += 1
            else:
                all_cost += verticalCut.pop() * (h_cnt + 1)
                v_cnt += 1
        if verticalCut:
            horizontalCut = verticalCut
            v_cnt = h_cnt
        while horizontalCut:
            all_cost += horizontalCut.pop() * (v_cnt + 1)
        return all_cost
