class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(), h.rend());
        sort(v.rbegin(), v.rend());
        long long ans = 0;
        long long i = 0, j = 0;
        while (i < m - 1 || j < n - 1) {
            if (i == m - 1) {
                ans += (long long)v[j] * (i + 1);
                j++;
                continue;
            }
            if (j == n - 1) {
                ans += (long long)h[i] * (j + 1);
                i++;
                continue;
            }
            if (h[i] > v[j]) {
                ans += (long long)h[i] * (j + 1);
                i++;
            }
            else {
                ans += (long long)v[j] * (i + 1);
                j++;
            }
        }
        return ans;
    }
};
