class Solution:
    def minimumCost(self, m: int, n: int, H: List[int], V: List[int]) -> int:
        h_need = 1
        v_need = 1

        res = 0

        H.sort()
        V.sort()

        while H or V:
            if H and V:
                if H[-1] < V[-1]:
                    res += V.pop() * v_need
                    h_need += 1
                else:
                    res += H.pop() * h_need
                    v_need += 1

            elif H:
                res += H.pop() * h_need
            else:
                res += V.pop() * v_need
                # verticalCut only
        return res
