class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        sort(hcut.begin(),hcut.end());
        sort(vcut.begin(),vcut.end());
        int vlen=vcut.size(),hlen=hcut.size();
        long long hcnt=1,vcnt=1;
        long long ans=0;
        while(vlen>0&&hlen>0){
            if(hcut[hlen-1]>=vcut[vlen-1]){
                ans+=(vcnt*hcut[hlen-1]);
                ++hcnt;
                --hlen;
            }else{
                ans+=(hcnt*vcut[vlen-1]);
                ++vcnt;
                --vlen;
            }
        }
        while(vlen>0){
            ans+=(hcnt*vcut[vlen-1]);
            --vlen;
        }
        while(hlen>0){
            ans+=(vcnt*hcut[hlen-1]);
            --hlen;
        }
        return ans;
    }
};
